mapper.select:
SELECT p.property_id AS property_id, p.title, f.funding_id AS funding_id, f.target_amount, f.current_amount, FLOOR((f.current_amount / f.target_amount) * 100) AS achievement_rate, (f.target_amount - f.current_amount) / 5000 AS remaining_shares, (f.target_amount - f.current_amount) AS remaining_amount, fo.share_count, (SELECT photo_url FROM property_photos WHERE property_id = p.property_id ORDER BY created_at ASC LIMIT 1) AS thumbnail_url FROM funding_order fo JOIN fundings f ON fo.funding_id = f.funding_id JOIN properties p ON f.property_id = p.property_id WHERE fo.user_id = #{userId}
AND fo.status = 'PENDING'
AND fo.status = 'REFUNDED' AND f.status = 'FAILED'
ORDER BY fo.created_at DESC LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO funding_order (
  user_id, funding_id, share_count, order_price
) VALUES (
           #{userId}, #{fundingId}, #{shareCount}, #{shareCount} * 5000
         )

mapper.update:
UPDATE funding_order
SET status = 'REFUNDED'
WHERE order_id = #{orderId}
